import logo from './logo.svg';
import './App.css';

import img from './img/jauge_dash.png';

const user = {
  name: "Michel",
  lastName: "Dupond",
  workout: {
    lastSession: {
      duration: 2,
      sport: "VTT",
      week: 42,
      calories: 400,
    }
  },
  sleep: [
    {
      date: 1636135998015,
      duration: 300,
      bpm: 60,
      percentageDeepSleep: 40,
      percentageLightSleep: 30,

    },  
  ]
}

const Card = (props) =>{
  return (<div className="bg-indigo-200 flex justify-center items-center p-2 m-2 rounded">
  {props.children}
  </div>);
}
function App() {
  return (
    <div className="flex items-center flex-col w-full">
      <header className="w-full text-center">
            DashBoard for a healthy life
      </header>
      <main>
        <div>
          Sleep quality and time to bed
          <Card>
            hello {user.name}
            <img src={img} />
          </Card>
          <Card>
            graphique sur la semaine
          </Card>
        </div>
        <div>
          Workout
          <Card>
            <>
            training <br />
            {user.workout.lastSession.sport} <br />
            week {user.workout.lastSession.week} <br />
            {user.workout.lastSession.duration} hours
            </>
          </Card>
          <Card>
            Diet and Calories <br />
            You burnt {user.workout.lastSession.calories} calories this week.
          </Card>
        </div>
        <div>
          Calculator of calories 
          //the user enters the composition of his meal (for example 200g of veal, 100g of rice, 60g of zuchinis...)

        </div>

      </main>
      <footer>

      </footer>
    </div>
  );
}

export default App;
